
var _ready = function(area){
	
	//获取区域
	var obj = $("#"+area);
	//获取区域里面的input
	var inputs = obj.find("input");
	inputs.each(function(){
		
		$(this).prop("readonly",true);
		//添加去边框样式
		$(this).addClass("dianji");
		//添加点击事件
		$(this).click(function(){
			_click(this);
		});
		//添加失去焦点事件
		$(this).blur(function(){
			_blur(this)
		});
		
	});
	
}


var _click = function(obj){
	
	//取消边框样式
	$(obj).removeClass("dianji");
	//取消只读
	$(obj).prop("readonly",false);
	//alert($("#selectShowBorder1").html());
	if( "selectShowBorder1" == $(obj).parent().attr("id")){
		$("#selectShowBorder1").removeClass(".dianji");
	}
	
};	

var _blur = function(obj){
	
	//添加去边框样式
	$(obj).addClass("dianji");
	//添加只读
	$(obj).prop("readonly",true);
	if( "selectShowBorder1" == $(obj).parent().attr("id")){
		$("#selectShowBorder1").addClass(".dianji");
	}
	
	
};	


//添加一行
 var _addtr = function (area){
	
	//获取区域
	var obj = $("#"+area);
	var temp = obj.children("tbody").children("tr:first").clone();
	temp.appendTo(obj.children("tbody"));
	obj.children("tbody").children("tr:last").show();
	
	$("div[class=selectbox]").each(function(){
		
		$(this).jQSelect({});
				
	});
	_ready("dataTableExample1");
	
}	

//删除行
var _deletetr = function(area){
	
	
	var obj = $("#"+area);
	var count = 0;	
	obj.find("tr").each(function(){
		
		var check = $(this).find(".selectable-all")
		if($(check).is(':checked')){
			count++;
		}
		
	})
	if(count == 0){
		
		window.top.layer.msg('你没有选择任何数据', {icon: 1});
		return;
		
	}
	
	window.top.layer.confirm(
		'确定删除选中记录？',
		{
		btn: ['确定', '取消'] // 按钮
		},
		function() {
			//获取区域
			var obj = $("#"+area);
			
			obj.find("tr").each(function(){
				
				var check = $(this).find(".selectable-all")
				console.log(check);
				if($(check).is(':checked')){
					$(this).remove();
				}
				
			})
			window.top.layer.msg('删除成功。', {icon: 1});
		},
		function() {
			
		}
    );

}

var _selectAll = function(temp,area){

	//获取区域
	var obj = $("#"+area);
	
	obj.find("tr").each(function(){
		
		//全选按钮
		//var check = $(temp).find(".selectable-all");
		//当前行
		var test = $(this).find(".selectable-all");
		
		if($(temp).prop("checked")){
			test.prop('checked',true)
		}else{
			test.prop('checked',false)
		}
	})

}
	